INSERT INTO chongdetang.culture (id, date, title, url, type, description, photo) VALUES (32, '2021-06-11', '拓片制作流程揭秘', 'https://www.cdtde.com/whzs/tuopiandiaoban/304.html', 'tpdk', '工具： 1，塑料工具箱一个，装工具（到处都有卖的，你们不必自己再去学塑料成型加工工艺了） 2，塑料喷壶一只，用来发纸（就是理发店喷水的，或者你们家喷花的） 3，棕刷一只，...', 'http://cdtde.com/images/defaultpic.gif');
