create table news
(
    id          int auto_increment
        primary key,
    date        date         null comment '文章发布日期',
    title       varchar(100) null comment '文章标题',
    photo       varchar(100) null comment '文章标题配图标识符',
    url         varchar(100) null comment '文章网址',
    type        varchar(100) null comment '子板块类型',
    description varchar(500) null comment '文章摘要'
)
    comment '新闻中心内容表';

